import pinPadCalculator from './'

const exampleInput = `
ULL
RRDDD
LURDL
UUUUD
`

const puzzleInput = `





`

describe('Bathroom code solver', () => {
  describe('square pin pad', () => {
    it('solves example in part 1', () => {
      expect(pinPadCalculator(exampleInput, 'square')).toEqual('1985')
    })
    it('solves puzzle in part 1', () => {
      expect(pinPadCalculator(puzzleInput, 'square')).toEqual('53255')
    })
  })
  describe('diamond pin pad', () => {
    it('solves example in part 2', () => {
      expect(pinPadCalculator(exampleInput, 'diamond')).toEqual('5DB3')
    })
    it('solves puzzle in part 2', () => {
      expect(pinPadCalculator(puzzleInput, 'diamond')).toEqual('7423A')
    })
  })
})
